import { Navigate} from "react-router-dom"
import { useParams} from "react-router-dom"
import { data } from '../datas/data'
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faStar } from '@fortawesome/free-solid-svg-icons'
import Carousel from '../components/Carousel'
import Collapse from '../components/Collapse'
import '../assets/styles/logement.scss'
import '../assets/styles/home.scss'
import { faAngleLeft, faAngleRight } from '@fortawesome/free-solid-svg-icons'

function Title() {
  const { id } = useParams()
  const bien = data.find((item) => item.id === id)
  if (!bien) { 
    return  <Navigate to='/Page404'/>
  }
  const host = bien.host


  const showTag = (tag) => {
    return tag.map((tag, index) => (
      <span key={index}>{tag}</span>
    ))
  }

  const starRating = (rating) => {
    return Array.from({ length: 5 }, (_, index) => (
      <FontAwesomeIcon icon={faStar} className={index < rating ? "RedStar" : null} key={index} />
    ))

  }
  let index=0;
  const slideShow = (direction)=>{

      switch (direction) {
          case "left":
              index--;
              if (index < 0) {
                  index = bien.pictures.length - 1;
              }
              break;
          case "right":
              index++;
              if (index > bien.pictures.length - 1) {
                  index = 0;
              }
              break;
          }
          console.log(bien.pictures[index])
        }
  return (


      <div className="all">
        <div className='carousel'>

<FontAwesomeIcon icon={faAngleLeft} className="arrow_left" onClick={()=>slideShow('left')} />
<FontAwesomeIcon icon={faAngleRight} className="arrow_right" onClick={()=>slideShow('right')}/>
<img src={bien.pictures[index]} alt='img' />
</div>

        <div className="info-proprio">
          <div className="title-tag">
            <h1>{bien.title} </h1>
            <div>
              <span>{bien.location}</span>
            </div>
            <div className='tags'>
            {showTag(bien.tags)}
          </div>
          </div>

          <div className='rating'>
          <div className='ProprioTitle'>
            <span>{host.name}</span>
            <img src={host.picture} alt='proprietaire' className='propImg' />
          </div>
          <div className="card-rating">
            {starRating(bien.rating)}
          </div>
        </div>



</div>

        <div className="box-info">

          <Collapse title='Description'>
            <p>{bien.description}</p>
          </Collapse>

          <Collapse title='Equipements'>
            <ul>
              {bien.equipments.map((equipments, index) => (
                <li key={index}>{equipments}</li>
              ))}
            </ul>
          </Collapse>
        </div>
      </div>

  )
}



export default Title
