function State() {

    const [isOpen, setIsOpen] = useState(false)


    return  (
        <div className='box'>

            <div className='box-title'>
                <span>Descriprion</span>
                <FontAwesomeIcon icon={isOpen ? faAngleDown : faAngleUp} onClick={()=>setIsOpen(!isOpen)} />
            </div>

            {isOpen && <div className='box-content'>oijji</div>}

        </div>
    ) 
}

export default State

