import { useParams } from "react-router-dom"
import {data} from '../datas/data'
import Header from '../components/Header'
import '../assets/styles/Bien.scss'
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faAngleDown, faAngleUp , faStar } from '@fortawesome/free-solid-svg-icons'

function Title() {
    const {id} = useParams()
    const bien = data.find((item)=>item.id===id)
    const host = bien.host

    const showTag =(tag) => {
    return tag.map((tag,index)=>(
    <span key={index}>{tag}</span>
    ))
  }

    const star = (rating) => {
      return Array.from({length:5},(_,index)=>(
        <FontAwesomeIcon icon={ faStar} className="RedStar"/>
      ))

    }

    return (
      <div>

        <Header/>

        <img src={bien.pictures[0]} alt='img'/>

        <div className="align">
          <div >
          <h1>{bien.title} </h1>
          <span>{bien.location}</span>
          </div>

          <div className='ProprioTitle'>
          <span>{host.name}</span>
          <img src={host.picture} alt='proprietaire' className='propImg'/>
          </div>
        </div>


        <div className="ligne">
        <div className='tags'>       
          {showTag(bien.tags)}
        </div>
        <div className="card-rating">
        {star(bien.rating)}
            </div>
        </div>

        <div className='description'>
          <span>Descriprion<FontAwesomeIcon icon={ faAngleDown}/></span>
          <span>Equipements<FontAwesomeIcon icon={ faAngleDown}/></span>
        </div>


        </div>
    )
  }



  export default Title
