import { useParams } from "react-router-dom"
import { data } from '../datas/data'
import Header from '../components/Header'
import '../assets/styles/Bien.scss'
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faAngleDown, faAngleUp, faAngleLeft, faAngleRight, faStar } from '@fortawesome/free-solid-svg-icons'
import { useState } from 'react'


function Title() {
  const { id } = useParams()
  const bien = data.find((item) => item.id === id)
  const host = bien.host
  const equipments = bien.equipments
  let i = 0;
  const showTag = (tag) => {
    return tag.map((tag, index) => (
      <span key={index}>{tag}</span>
    ))
  }

  const starRating = (rating) => {
    return Array.from({ length: 5 }, (_, index) => (
      <FontAwesomeIcon icon={faStar} className={index < rating ? "RedStar" : null} key={index} />
    ))

  }

  const slideShow= (direction) => {
  if(direction==='left'){

  }
  console.log(bien.pictures[i+1])
  }
  return (
    <div>

      <Header />

      <div className='carousel'>
        <button  onClick={slideShow('left')}>
        <FontAwesomeIcon icon={faAngleLeft} className="arrow_left" />
        </button>
        <button>
        <FontAwesomeIcon icon={faAngleRight} className="arrow_right" onClick={slideShow('right')}/>
        </button>
        <img src={bien.pictures[i]} alt='img' />

      </div>
      <div className="align">
        <div >
          <h1>{bien.title} </h1>
          <span>{bien.location}</span>
        </div>

        <div className='ProprioTitle'>
          <span>{host.name}</span>
          <img src={host.picture} alt='proprietaire' className='propImg' />
        </div>
      </div>


      <div className="ligne">
        <div className='tags'>
          {showTag(bien.tags)}
        </div>
        <div className="card-rating">
          {starRating(bien.rating)}
        </div>
      </div>

      <div className='description'>
        <div >
        <span>Descriprion<FontAwesomeIcon icon={faAngleDown} /></span>
        <p>{bien.description}</p>
        </div>

        <div>
        <span>Equipements<FontAwesomeIcon icon={faAngleDown} /></span>

        <ul>
        {equipments.map((e , index ) => (
                <li key={index}>{e}</li>
            ))}
        </ul>



      </div>
      </div>

    </div>
  )
}



export default Title
