       console.log(index)
    }

    return (
        <div className='carousel'>

            <FontAwesomeIcon icon={faAngleLeft} className="arrow_left" onClick={()=>slideShow('left')} />
            <FontAwesomeIcon icon={faAngleRight} className="arrow_right" onClick={()=>slideShow('right')}/>
            <img src={photo[currentIndex]} alt='img' />

        </div>
    )
}


export default Carousel
